import React, { useState } from 'react';
import './App.css';
import Book from './assets/Book.png';
import DefaultProfile from './assets/default-profile.png';

function App() {
  const [storyContent, setStoryContent] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleImageGeneration = () => {
    // 이미지 생성 로직을 여기에 추가할 수 있습니다.
    // 예를 들어, storyContent를 사용하여 API 호출 등을 수행할 수 있습니다.
    console.log('이미지 생성 요청:', storyContent);
    alert('이미지 생성 요청: ' + storyContent); // 임시 알림
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app-container">
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="logo">
          {/* <img src={logo} alt="Storybook Creation Logo" /> 로고 이미지 제거 */}
        </div>
        <div className="sidebar-icons">
          <button className="icon-button new-chat"><span className="icon">+</span><span className="label">새로운 동화 생성</span></button>
          <button className="icon-button new-image"><span className="icon">🖼️</span><span className="label">이미지 생성</span></button>
          <div className="history-section">
            <div className="history-header"><span className="icon"></span><span className="label">- 동화 목록 -</span></div>
            <div className="history-list">
              <div className="history-item"><span className="history-title">생성된 동화가 없습니다</span><span className="history-date">-</span></div>
              {/* 여기에 동화 목록을 렌더링할 수 있습니다 */}
            </div>
          </div>
        </div>
      </div>
      <button className="toggle-button" onClick={toggleSidebar}>
        <img src={DefaultProfile} alt="Toggle Sidebar" className="toggle-icon"/>
      </button>
      <div className="main">
        <button className="user-menu-button">
          <img alt="user" className="user-icon" src="/static/media/default-profile.339ef8a97c07c5d2d08b.png" />
        </button>
        <div className="content">
          <h1>어떤 이미지를 생성하시겠습니까?</h1>
        </div>
        <footer className="prompt">
          <textarea
            placeholder="동화 내용을 입력하세요.(그에 맞는 이미지가 생성됩니다.)"
            value={storyContent}
            onChange={(e) => setStoryContent(e.target.value)}
          ></textarea>
          <button className="" disabled={!storyContent}>
            이미지 생성
          </button>
        </footer>
      </div>
    </div>
  );
}

export default App;
