import React, { useState } from 'react';
import './App.css';
import Book from './assets/Book.png';
import DefaultProfile from './assets/default-profile.png';

function App() {
  const [storyContent, setStoryContent] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [currentView, setCurrentView] = useState('default');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [messages, setMessages] = useState([]);

  const showCustomAlert = (message) => {
    setAlertMessage(message);
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  };

  const handleImageGeneration = async () => {
    if (!storyContent.trim()) return;

    const userMessage = {
      type: 'user',
      content: storyContent,
    };
    setMessages((prevMessages) => [...prevMessages, userMessage]);
    setStoryContent('');
    setCurrentView('chat');

    setIsLoading(true);
    setError(null);

    try {
      console.log('API Ìò∏Ï∂ú ÏãúÏûë:', storyContent);

      const response = await fetch('http://localhost:8000/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: storyContent,
          type: currentView === 'image' ? 'image' : 'story'
        })
      });

      if (!response.ok) {
        const errorData = await response.text();
        console.error('API ÏùëÎãµ ÏóêÎü¨:', errorData);
        const errorMessage = `ÏÉÅÌÉú ÏΩîÎìú: ${response.status}\nÏóêÎü¨ Î©îÏãúÏßÄ: ${errorData}`;
        showCustomAlert(errorMessage);
        throw new Error(errorMessage);
      }

      const data = await response.json();
      console.log('API ÏùëÎãµ ÏÑ±Í≥µ:', data);

      const assistantMessage = {
        type: 'assistant',
        content: currentView === 'image' ? data.imageUrl : data.story,
      };
      setMessages((prevMessages) => [...prevMessages, assistantMessage]);

    } catch (err) {
      console.error('API Ìò∏Ï∂ú ÏÉÅÏÑ∏ ÏóêÎü¨:', err);
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const handleNewStory = () => {
    setCurrentView('story');
    setStoryContent('');
    setMessages([]);
  };

  const handleNewImage = () => {
    setCurrentView('image');
    setStoryContent('');
    setMessages([]);
  };

  return (
    <div className="app-container">
      {showAlert && (
        <div className="custom-alert">
          <div className="alert-content">
            {alertMessage.split('\n').map((line, index) => (
              <p key={index}>{line}</p>
            ))}
          </div>
        </div>
      )}
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="logo">
          {/* <img src={logo} alt="Storybook Creation Logo" /> Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Ï†úÍ±∞ */}
        </div>
        <div className="sidebar-icons">
          <button className="icon-button new-chat" onClick={handleNewStory}>
            <span className="icon">+</span>
            <span className="label">ÏÉàÎ°úÏö¥ ÎèôÌôî ÏÉùÏÑ±</span>
          </button>
          <button className="icon-button new-image" onClick={handleNewImage}>
            <span className="icon">üñºÔ∏è</span>
            <span className="label">Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±</span>
          </button>
          <div className="history-section">
            <div className="history-header">
              <span className="icon"></span>
              <span className="label">- ÎèôÌôî Î™©Î°ù -</span>
            </div>
            <div className="history-list">
              <div className="history-item">
                <span className="history-title">ÏÉùÏÑ±Îêú ÎèôÌôîÍ∞Ä ÏóÜÏäµÎãàÎã§</span>
                <span className="history-date">-</span>
              </div>
              {/* Ïó¨Í∏∞Ïóê ÎèôÌôî Î™©Î°ùÏùÑ Î†åÎçîÎßÅÌï† Ïàò ÏûàÏùå. */}
            </div>
          </div>
        </div>
      </div>
      <button className="toggle-button" onClick={toggleSidebar}>
        <img src={Book} alt="Toggle Sidebar" className="toggle-icon"/>
      </button>
      <div className="main">
        <button className="toggle-button" onClick={toggleProfile} style={{ left: 'auto', right: '20px' }}>
          <img src={DefaultProfile} alt="Toggle Profile" className="toggle-icon"/>
        </button>
        <div className="content">
          {currentView === 'chat' ? (
            <div className="chat-container">
              <div className="messages">
                {messages.map((message, index) => (
                  <div key={index} className={`message ${message.type}`}>
                    <div className="message-content">
                      {message.type === 'assistant' && currentView === 'image' ? (
                        <img src={message.content} alt="Generated" className="generated-image" />
                      ) : (
                        <p>{message.content}</p>
                      )}
                    </div>
                  </div>
                ))}
                {isLoading && (
                  <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...</p>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <h1>{currentView === 'image' ? 'Ïñ¥Îñ§ Ïù¥ÎØ∏ÏßÄÎ•º ÏÉùÏÑ±ÌïòÏãúÍ≤†ÏäµÎãàÍπå?' : 'ÏÑ∏ÏÉÅÏóêÏÑú ÌïòÎÇòÎøêÏù∏ ÎãπÏã†ÎßåÏùò ÎèôÌôîÏ±ÖÏùÑ ÏÉùÏÑ±Ìï¥ Î≥¥ÏÑ∏Ïöî!'}</h1>
          )}
        </div>
        <footer className="prompt">
          <textarea
            placeholder={currentView === 'image' ? 
              "ÏÉùÏÑ±Îêú ÎèôÌôî ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî.(Í∑∏Ïóê ÎßûÎäî Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏÉùÏÑ±Îê©ÎãàÎã§.)" : 
              "Î¨¥ÏóáÏù¥Îì† ÏÉùÏÑ±ÌïòÍ≥† Ïã∂ÏùÄ ÎèôÌôîÏ±ÖÏùò ÎåÄÎûµÏ†ÅÏù∏ ÏãúÎÇòÎ¶¨Ïò§Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî. (ÏµúÏÜåÌïú Îì±Ïû•Ïù∏Î¨º, Îì±Ïû•Ïù∏Î¨º Ïàò, ÎåÄÎûµÏ†ÅÏù∏ Ï§ÑÍ±∞Î¶¨, ÏõêÌïòÎäî Í≤∞ÎßêÏùò Î∞©Ìñ•ÏÑ± Îì±)"}
            value={storyContent}
            onChange={(e) => setStoryContent(e.target.value)}
          ></textarea>
          <button 
            className={isLoading ? 'loading' : ''} 
            disabled={!storyContent || isLoading}
            onClick={handleImageGeneration}
          >
            {currentView === 'image' ? 'Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±' : 'ÎèôÌôî ÏÉùÏÑ±'}
          </button>
        </footer>
      </div>
    </div>
  );
}

export default App;
