import React, { useState } from 'react';
import './App.css';
import Book from './assets/Book.png';

function App() {
  const [storyContent, setStoryContent] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleImageGeneration = () => {
    // 이미지 생성 로직을 여기에 추가할 수 있습니다.
    // 예를 들어, storyContent를 사용하여 API 호출 등을 수행할 수 있습니다.
    console.log('이미지 생성 요청:', storyContent);
    alert('이미지 생성 요청: ' + storyContent); // 임시 알림
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app-container">
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="logo">
          {/* <img src={logo} alt="Storybook Creation Logo" /> 로고 이미지 제거 */}
        </div>
        <button className="sidebar-button">새로운 동화 생성</button>
        <button className="sidebar-button image-button">이미지 생성</button>
        <div className="sidebar-section-title">- 동화 목록 -</div>
        <div className="story-list-empty">생성된 동화가 없습니다</div>
        {/* 여기에 동화 목록을 렌더링할 수 있습니다 */}
      </div>
      <div className="main-content">
        <button className="sidebar-toggle-button" onClick={toggleSidebar}>
          <img src={Book} alt="Toggle Sidebar" />
        </button>
        <div className="main-title">어떤 이미지를 생성하시겠습니까?</div>
        <div className="input-area">
          <textarea
            className="story-input"
            placeholder="동화 내용을 입력하세요. (그에 맞는 이미지가 생성됩니다.)"
            value={storyContent}
            onChange={(e) => setStoryContent(e.target.value)}
          ></textarea>
          <button className="generate-image-button" onClick={handleImageGeneration}>
            이미지 생성
          </button>
        </div>
      </div>
      <div className="user-icon"></div>
    </div>
  );
}

export default App;
