import React, { useState } from 'react';
import './App.css';
import Book from './assets/Book.png';
import DefaultProfile from './assets/default-profile.png';

function App() {
  const [storyContent, setStoryContent] = useState('');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [currentView, setCurrentView] = useState('story'); // Ï¥àÍ∏∞Í∞íÏùÑ 'story'Î°ú Î≥ÄÍ≤Ω

  const handleImageGeneration = () => {
    // Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ± Î°úÏßÅÏùÑ Ïó¨Í∏∞Ïóê Ï∂îÍ∞ÄÌï† Ïàò ÏûàÏäµÎãàÎã§.
    // ÏòàÎ•º Îì§Ïñ¥, storyContentÎ•º ÏÇ¨Ïö©ÌïòÏó¨ API Ìò∏Ï∂ú Îì±ÏùÑ ÏàòÌñâÌï† Ïàò ÏûàÏäµÎãàÎã§.
    console.log('Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ± ÏöîÏ≤≠:', storyContent);
    alert('Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ± ÏöîÏ≤≠: ' + storyContent); // ÏûÑÏãú ÏïåÎ¶º
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const toggleProfile = () => {
    setIsProfileOpen(!isProfileOpen);
  };

  const handleNewStory = () => {
    setCurrentView('story');
    setStoryContent('');
  };

  const handleNewImage = () => {
    setCurrentView('image');
    setStoryContent('');
  };

  return (
    <div className="app-container">
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="logo">
          {/* <img src={logo} alt="Storybook Creation Logo" /> Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Ï†úÍ±∞ */}
        </div>
        <div className="sidebar-icons">
          <button className="icon-button new-chat" onClick={handleNewStory}>
            <span className="icon">+</span>
            <span className="label">ÏÉàÎ°úÏö¥ ÎèôÌôî ÏÉùÏÑ±</span>
          </button>
          <button className="icon-button new-image" onClick={handleNewImage}>
            <span className="icon">üñºÔ∏è</span>
            <span className="label">Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±</span>
          </button>
          <div className="history-section">
            <div className="history-header">
              <span className="icon"></span>
              <span className="label">- ÎèôÌôî Î™©Î°ù -</span>
            </div>
            <div className="history-list">
              <div className="history-item">
                <span className="history-title">ÏÉùÏÑ±Îêú ÎèôÌôîÍ∞Ä ÏóÜÏäµÎãàÎã§</span>
                <span className="history-date">-</span>
              </div>
              {/* Ïó¨Í∏∞Ïóê ÎèôÌôî Î™©Î°ùÏùÑ Î†åÎçîÎßÅÌï† Ïàò ÏûàÏäµÎãàÎã§ */}
            </div>
          </div>
        </div>
      </div>
      <button className="toggle-button" onClick={toggleSidebar}>
        <img src={Book} alt="Toggle Sidebar" className="toggle-icon"/>
      </button>
      <div className="main">
        <button className="toggle-button" onClick={toggleProfile} style={{ left: 'auto', right: '20px' }}>
          <img src={DefaultProfile} alt="Toggle Profile" className="toggle-icon"/>
        </button>
        <div className="content">
          <h1>{currentView === 'image' ? 'Ïñ¥Îñ§ Ïù¥ÎØ∏ÏßÄÎ•º ÏÉùÏÑ±ÌïòÏãúÍ≤†ÏäµÎãàÍπå?' : 'ÎãπÏã†ÎßåÏùò ÎèôÌôîÏ±ÖÏùÑ ÏÉùÏÑ±Ìï¥ Î≥¥ÏÑ∏Ïöî!'}</h1>
        </div>
        <footer className="prompt">
          <textarea
            placeholder={currentView === 'image' ? 
              "ÎèôÌôî ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî.(Í∑∏Ïóê ÎßûÎäî Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏÉùÏÑ±Îê©ÎãàÎã§.)" : 
              "ÎèôÌôî ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî."}
            value={storyContent}
            onChange={(e) => setStoryContent(e.target.value)}
          ></textarea>
          <button className="" disabled={!storyContent}>
            {currentView === 'image' ? 'Ïù¥ÎØ∏ÏßÄ ÏÉùÏÑ±' : 'ÎèôÌôî ÏÉùÏÑ±'}
          </button>
        </footer>
      </div>
    </div>
  );
}

export default App;
